import sys
import traceback
import logger_util

sys.path.insert(0,AVX::HELPER)
sys.path.insert(0,AVX::DEPENDENCIES)

logger = logger_util.get_logger('Firewall Panorama Address')


'''

This task is to create the Address Object in the Panorama Device
#FIXME: Prevalidation and Postvalidation to be done
#FIXME: if no input received fro the boolean flags, then the device property is set to 'no'

'''
class FirewallPanoramaAddress(object):

    @classmethod
    def clean_input_data(self,input_dict):
        logger.info("Input Received Before clean : "+str(input_dict))

        input_dict['name'] = self.handle_strip(input_dict['name'])
        input_dict['device_group'] = self.handle_strip(input_dict['device_group'])
        input_dict['description'] = self.handle_strip(input_dict['description'])
        input_dict['tag'] = self.handle_strip(input_dict['tag'])
        input_dict['address_type'] = self.handle_strip(input_dict['address_type'])
        input_dict['address_value'] = self.handle_strip(input_dict['address_value'])
        input_dict['device_name'] = self.handle_strip(input_dict['device_name'])
        input_dict['operation_type'] = self.handle_strip(input_dict['operation_type'])
        logger.info("Input Received After clean : "+str(input_dict))

    #FIXME: Only generic validation available, specific to vendor to be added
    @classmethod
    def validate_input_data(self,input_dict):
        logger.info("Validating the Input Data")
        status = True

        status = status and self.mandatory_string_validation(input_dict['name'],"name",True)
        status = status and self.mandatory_string_validation(input_dict['device_group'],"device_group",True)
        status = status and self.mandatory_string_validation(input_dict['device_name'],"device_name",True)
        status = status and self.mandatory_string_validation(input_dict['address_type'],"address_type",True)
        status = status and self.mandatory_string_validation(input_dict['address_value'],"address_value",True)

        if not status:
            logger.info("INPUT VALIDATION FAILED")
        else:
            logger.info("INPUT VALIDATION SUCCESS")

        return status

    @classmethod
    def validation_fail_output(self):
        output = {}
        output["status"] = "failed"
        output["message"] = "Validation failed"
        logger.info("output : "+str(output))
        return output

    def __init__(self,input_dict):
        logger.info("Creating Object with input :"+str(input_dict))
        self.name = input_dict['name']
        self.device_group = input_dict['device_group']
        self.description = input_dict['description']
        self.is_disable_override = input_dict['is_disable_override']
        self.tag = input_dict['tag']
        self.address_type = input_dict['address_type']
        self.address_value = input_dict['address_value']
        self.operation_type = input_dict['operation_type']
        self.device_name = input_dict['device_name']
        self.is_rollback = input_dict['is_rollback']
        self.rollback_input = input_dict['rollback_input']
        self.is_deferred = input_dict['is_deferred']

    def get_input_dict(self):
        output = {}
        output['name'] = self.name
        output['device_group'] = self.device_group
        output['description'] = self.description
        output['is_disable_override'] = self.is_disable_override
        output['tag'] = self.tag
        output['address_type'] = self.address_type
        output['address_value'] = self.address_value     
        output['operation_type'] = self.operation_type
        output['device_name'] = self.device_name
        output['is_rollback'] = self.is_rollback
        output['rollback_input'] = self.rollback_input
        output['is_deferred'] = self.is_deferred
        return output

    def create(self):
        logger.info("Creation Started")
        try:
            if self.is_deferred:
                return self.create_deferred()
            else:
                return self.create_immediate()
        except Exception as exception:
            output = {}
            error_trace = traceback.format_exec()
            logger.info("error_trace :"+str(error_trace))
            output["status"] = "failed"
            output["error"] = str(exception)
            output["error_trace"] = str(error_trace)
            return output

    # FIXME: support not provided now
    def create_immediate(self):
        logger.info("Create Immediate not supported ")
        return {"status":"failed","message":"not supported"}

    def create_deferred(self):
        output_commands = []
        command = "set device-group "+self.device_group+" "

        if self.name:
            command += "address "+self.name+" "

        if self.description:
            command += "description '"+self.description+"' "

        command += "disable-override "+("yes" if self.is_disable_override else "no")+" "

        if self.tag:
            command += "tag "+self.tag+" "

        if self.address_type and self.address_value:
            command += self.address_type+" "+self.address_value+" "

        output_commands.append(command)

        output = {}
        output["status"] = "success"
        output['commands'] = output_commands
        output['rollback'] = self.get_input_dict()
        return output


    def delete(self):
        logger.info("Deletion Started")
        try:
            if self.is_deferred:
                return self.delete_deferred()
            else:
                return self.delete_immediate()
        except Exception as exception:
            output = {}
            error_trace = traceback.format_exec()
            logger.info("error_trace :"+str(error_trace))
            output["status"] = "failed"
            output["error"] = str(exception)
            output["error_trace"] = str(error_trace)
            return output

    def delete_immediate(self):
        logger.info("Delete Immediate not supported ")
        return {"status":"failed","message":"not supported"}

    # FIXME: Delete rollback to be handled by fetching all the complete object details from the device
    def delete_deferred(self):
        output_commands = []
        command = "delete device-group "+self.device_group+" "

        if self.name:
            command += "address "+self.name+" "
        output_commands.append(command)
        output = {}
        output["status"] = "success"
        output["commands"] = output_commands
        output['rollback'] = []
        return output


    # FIXME: Prevalidation functionality to be added
    @classmethod
    def carry_out_pre_validation_activity_for_tabular_input(self, input_dict):
        activity_output = []
        output_dict = {}
        output_dict['device_name'] =  input_dict['device_name']
        output_dict['operation_type'] =  input_dict['operation_type']
        output_dict['data'] = []
        output_dict['commands'] = ["show device-group " +input_dict['device_group']+" address "+input_dict['name']]
        activity_output.append(output_dict)

        return activity_output

# FIXME: PostValidation functionality to be added

    def carry_out_post_validation_activity_for_tabular_input(self):
        activity_output = []
        output_dict = {}
        output_dict['device_name'] = self.device_name
        output_dict['operation_type'] = self.operation_type
        output_dict['data'] = []
        output_dict['commands'] = ["show device-group " +self.device_group+" address "+self.name]
        activity_output.append(output_dict)
        return activity_output

    # FIXME: Partial Rollback functionality to be added

    def generate_partial_rollback_commands(self, rollback_input):
        activity_output = []
        for rollback_dict in rollback_input:

            output_dict = {}
            output_dict['device_name'] = rollback_dict['device_name']
            output_dict['operation_type'] = rollback_dict['operation_type']
            output_dict['data'] = []
            if rollback_dict['operation_type'] == "create":
                output_dict['commands'] = self.delete_deferred()['commands']
            elif rollback_dict['operation_type'] == "delete":
                output_dict['commands'] = self.create_deferred()['commands']
            else:
                output_dict['commands'] = []
            activity_output.append(output_dict)
        return activity_output


    @classmethod
    def handle_strip(self,input):
        if type(input).__name__ == 'str':
            return input.strip() if (input!=None and input!='') else input
        return input

    @classmethod
    def mandatory_string_validation(self,input,key,is_error_update):
        output = input != None and input != ''
        if not output and is_error_update:
            logger.info(key + " Validation Failed with "+str(input))    
        return output
