from appviewx import db_connection
connect_db = db_connection()


def devicename_to_id(devicename):
    db_filter = {'name': devicename}
    if devicename:
        deviceid = connect_db.appviewx.device.find(db_filter)
        for device in deviceid:
            return device['_id']
    else:
        return 'invalid parameter'

def getGTMDataCenterListByDevice(devicename='None'):
    '''
    to get the list of GTM Data Centers based on the input device name

    '''

    datacenter_list = []
    device_id = devicename_to_id(devicename)
    db_filter = {'deviceId': device_id}
    if not devicename == 'None':
        adc_datacenter_list = connect_db.appviewx.gdc.find(db_filter)
        for device in adc_datacenter_list:
            datacenter_list.append(device)
        return datacenter_list
    else:
        return 'Invalid parameter'
