from appviewx import db_connection
connect_db = db_connection()


def devicename_to_id(devicename):
    db_filter = {'name': devicename}
    if devicename:
        deviceid = connect_db.appviewx.device.find(db_filter)
        for device in deviceid:
            return device['_id']
    else:
        return 'invalid parameter'

def getGTMMonitorListByDevice(devicename='None'):
    '''
     to get the list of GTM monitors based on the input device name

    '''

    gtm_monitor_list = []
    device_id = devicename_to_id(devicename)
    db_filter = {'deviceId': device_id}
    if not devicename == 'None':
        adc_gtm_monitor_list = connect_db.appviewx.gm.find(db_filter)
        for device in adc_gtm_monitor_list:
            gtm_monitor_list.append(device)
        return gtm_monitor_list
    else:
        return 'Invalid parameter'
