from appviewx import db_connection
connect_db = db_connection()


def getGTMPoolListByDevice(devicename='None'):
    '''
    To get GTM pool list based on the input device name

    '''

    gtm_pool_list = []
    db_filter = {'deviceName': devicename, 'code': 'gp'}
    if not devicename == 'None':
        adc_gtm_pool_list = connect_db.appviewx.ro.find(db_filter)
        for device in adc_gtm_pool_list:
            gtm_pool_list.append(device)
        return gtm_pool_list
    else:
        return 'Invalid parameter'
