from appviewx import db_connection
connect_db = db_connection()



def getGTMWideIpListByDevice(devicename='None'):
    '''
    To get GTM wideip list based on the input device name

    '''

    gtm_wideip_list = []
    db_filter = {'deviceName': devicename, 'code': 'gw'}
    if not devicename == 'None':
        adc_gtm_wideip_list = connect_db.appviewx.ro.find(db_filter)
        for device in adc_gtm_wideip_list:
            gtm_wideip_list.append(device)
        return gtm_wideip_list
    else:
        return 'Invalid parameter'
