from appviewx import db_connection
connect_db = db_connection()


def devicename_to_id(devicename):
    db_filter = {'name': devicename}
    if devicename:
        deviceid = connect_db.appviewx.device.find(db_filter)
        for device in deviceid:
            return device['_id']
    else:
        return 'invalid parameter'

def getLTMNodeListByDevice(devicename='None'):
    '''
    to get the list of LTM node based on the input device name

    '''

    ltm_node_list = []
    device_id = devicename_to_id(devicename)
    db_filter = {'deviceId': device_id}
    if not devicename == 'None':
        adc_ltm_node_list = connect_db.appviewx.ln.find(db_filter)
        for device in adc_ltm_node_list:
            ltm_node_list.append(device)
        return ltm_node_list
    else:
        return 'Invalid parameter'
