from appviewx import db_connection
connect_db = db_connection()

def getLTMPoolListByDevice(devicename='None'):
    '''
    To get the list of LTM Pool based on the input device name

    '''

    ltm_pool_list = []
    db_filter = {'deviceName': devicename, 'code': 'lp'}
    if not devicename == 'None':
        adc_ltm_pool_list = connect_db.appviewx.ro.find(db_filter)
        for device in adc_ltm_pool_list:
            ltm_pool_list.append(device)
        return ltm_pool_list
    else:
        return 'Invalid parameter'
