from appviewx import db_connection
connect_db = db_connection()


def devicename_to_id(devicename):
    db_filter = {'name': devicename}
    if devicename:
        deviceid = connect_db.appviewx.device.find(db_filter)
        for device in deviceid:
            return device['_id']
    else:
        return 'invalid parameter'
def getLTMSnatPoolListByDevice(devicename='None'):
    '''
    to get the list of LTM snat pool based on the input device name
    '''

    ltm_snat_pool_list = []
    device_id = devicename_to_id(devicename)
    db_filter = {'deviceId': device_id}
    if not devicename == 'None':
        adc_ltm_snat_pool_list = connect_db.appviewx.ls.find(db_filter)
        for device in adc_ltm_snat_pool_list:
            ltm_snat_pool_list.append(device)
        return ltm_snat_pool_list
    else:
        return 'Invalid parameter'
