from appviewx import db_connection
connect_db = db_connection()

def devicename_to_id(devicename):
    db_filter = {'name': devicename}
    if devicename:
        deviceid = connect_db.appviewx.device.find(db_filter)
        for device in deviceid:
            return device['_id']
    else:
        return 'invalid parameter'

def getLTMTrafficClassListByDevice(devicename='None'):
    '''
    to get the list of LTM traffic class based on the input device name

    '''

    ltm_traffic_class_list = []
    device_id = devicename_to_id(devicename)
    db_filter = {'deviceId': device_id}
    if not devicename == 'None':
        adc_ltm_traffic_class_list = connect_db.appviewx.ltc.find(db_filter)
        for device in adc_ltm_traffic_class_list:
            ltm_traffic_class_list.append(device)
        return ltm_traffic_class_list
    else:
        return 'Invalid parameter'
