from appviewx import db_connection
connect_db = db_connection()



def getLTMVIPListByDevice(devicename='None'):
    '''
    To get the list of LTM VIP based on the input device name

    '''

    ltm_vip_list = []
    db_filter = {'deviceName': devicename, 'code': 'vs'}
    if not devicename == 'None':
        adc_ltm_vip_list = connect_db.appviewx.ro.find(db_filter)
        for device in adc_ltm_vip_list:
            ltm_vip_list.append(device)
        return ltm_vip_list
    else:
        return 'Invalid parameter'
